mapper.select:
select

from (
             select *,
                    row_number() over (partition by `code` order by version desc) as rn
             from flink_job
             ) t
        where rn = 1

    AND t.`type` = #{param.type}


    AND t.`name` like CONCAT('%', #{param.name}, '%')

SELECT t1.*, t2.id AS artifact_jar_id, t2.version AS artifact_jar_version, t2.flink_version AS artifact_jar_flink_version, t2.entry_class AS artifact_jar_entry_class, t2.file_name AS artifact_jar_file_name, t2.path AS artifact_jar_path, t3.id AS artifact_id, t3.`name` AS artifact_name, t3.type AS artifact_type, t3.remark AS artifact_remark, t4.id AS config_id, t4.`name` AS config_name, t4.flink_version AS config_flink_version, t4.resource_provider AS config_resource_provider, t4.deploy_mode AS config_deploy_mode, t4.flink_release_id AS config_flink_release_id, t4.cluster_credential_id AS config_cluster_credential_id, t4.config_options AS config_config_options, t4.remark AS config_remark, t5.id AS instance_id, t5.flink_cluster_config_id AS instance_flink_cluster_config_id, t5.`name` AS instance_name, t5.cluster_id AS instance_cluster_id, t5.web_interface_url AS instance_web_interface_url, t5.`status` AS instance_status, t5.remark AS instance_remark, t6.id AS job_instance_id, t6.flink_job_code AS job_instance_flink_job_code, t6.flink_job_version AS job_instance_flink_job_version, t6.job_id AS job_instance_job_id, t6.job_name AS job_instance_job_name, t6.job_state AS job_instance_job_state, t6.cluster_id AS job_instance_cluster_id, t6.web_interface_url AS job_instance_web_interface_url, t6.cluster_status AS job_instance_cluster_status, t6.start_time AS job_instance_start_time, t6.end_time AS job_instance_end_time, t6.duration AS job_instance_duration FROM ( select *, row_number() over (partition by `code` order by version desc) as rn from flink_job ) t1 JOIN flink_artifact_jar t2 ON t1.flink_artifact_id = t2.id JOIN flink_artifact t3 ON t2.flink_artifact_id = t3.id JOIN flink_cluster_config t4 ON t1.flink_cluster_config_id = t4.id LEFT JOIN flink_cluster_instance t5 ON t1.flink_cluster_instance_id = t5.id LEFT JOIN flink_job_instance t6 on t1.`code` = t6.flink_job_code and t1.version = t6.flink_job_version
t1.rn = 1 AND t1.`type` = #{param.type}
AND t1.`name` like CONCAT('%', #{param.name}, '%')
AND t1.flink_cluster_config_id = #{param.flinkClusterConfigId}
AND t1.flink_cluster_instance_id = #{param.flinkClusterInstanceId}
